import React from 'react';
import { motion } from 'framer-motion';
import { useParams, Navigate } from 'react-router-dom';

const ProfilePage = () => {
  const { profileType } = useParams();

  const profileInfo = {
    Recruiter: {
      description: "Hi, I'm [Your Name]! I'm a passionate software developer with expertise in full-stack development. I specialize in building scalable web applications using modern technologies like React, Node.js, and cloud services. I'm currently seeking opportunities to contribute to innovative projects and grow as a developer.",
      projects: [
        {
          title: "Netflix Portfolio Clone",
          description: "A responsive Netflix-inspired portfolio showcasing my projects and skills",
          technologies: ["React", "Tailwind CSS", "Framer Motion"],
          image: "/project1.jpg"
        },
        // Add more projects here
      ]
    },
    Viewer: {
      description: "Welcome to my portfolio! I'm [Your Name], a creative developer who loves building beautiful and functional web applications. I believe in clean code, user-centered design, and continuous learning.",
      projects: [
        {
          title: "Interactive Dashboard",
          description: "A real-time data visualization dashboard for analytics",
          technologies: ["React", "D3.js", "Node.js"],
          image: "/project2.jpg"
        },
        // Add more projects here
      ]
    },
    Adventurer: {
      description: "Hey there! I'm [Your Name], an adventurous developer who loves exploring new technologies and pushing the boundaries of what's possible on the web. I'm always excited to take on new challenges and learn from every project.",
      projects: [
        {
          title: "AI-Powered Chat Application",
          description: "A real-time chat app with AI integration for smart responses",
          technologies: ["React", "OpenAI API", "WebSocket"],
          image: "/project3.jpg"
        },
        // Add more projects here
      ]
    }
  };

  // If the profile type is not valid, redirect to home
  if (!profileInfo[profileType]) {
    return <Navigate to="/" replace />;
  }

  const currentProfile = profileInfo[profileType];

  return (
    <motion.div 
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      className="min-h-screen bg-black text-white p-8"
    >
      {/* Profile Description Section */}
      <motion.div 
        initial={{ y: 20 }}
        animate={{ y: 0 }}
        className="max-w-4xl mx-auto mb-12"
      >
        <h1 className="text-5xl font-bold mb-6">{profileType}</h1>
        <p className="text-xl text-gray-300 leading-relaxed">
          {currentProfile.description}
        </p>
      </motion.div>

      {/* Projects Section */}
      <div className="max-w-7xl mx-auto">
        <h2 className="text-3xl font-bold mb-8">Featured Projects</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          {currentProfile.projects.map((project, index) => (
            <motion.div
              key={index}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: index * 0.1 }}
              className="bg-gray-900 rounded-lg overflow-hidden hover:scale-105 transform transition duration-300"
            >
              <div className="aspect-video bg-gray-800">
                {/* Add project image here */}
                <div className="w-full h-full flex items-center justify-center text-gray-500">
                  Project Image
                </div>
              </div>
              <div className="p-6">
                <h3 className="text-xl font-bold mb-2">{project.title}</h3>
                <p className="text-gray-400 mb-4">{project.description}</p>
                <div className="flex flex-wrap gap-2">
                  {project.technologies.map((tech, techIndex) => (
                    <span
                      key={techIndex}
                      className="px-3 py-1 bg-gray-800 rounded-full text-sm"
                    >
                      {tech}
                    </span>
                  ))}
                </div>
              </div>
            </motion.div>
          ))}
        </div>
      </div>
    </motion.div>
  );
};

export default ProfilePage;
