import React from "react";
import { useNavigate } from "react-router-dom";
import { motion } from "framer-motion";

const profiles = [
  {
    name: "Recruiter",
    icon: "ðŸ‘”",
    description: "View my professional experience and skills"
  },
  {
    name: "Viewer",
    icon: "ðŸ‘€",
    description: "Explore my creative projects and designs"
  },
  {
    name: "Adventurer",
    icon: "ðŸš€",
    description: "Discover my experimental and innovative work"
  }
];

const ProfileSelector = () => {
  const navigate = useNavigate();

  const handleProfileSelect = (profile) => {
    navigate(`/profile/${profile.name}`);
  };

  return (
    <motion.div 
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      className="min-h-screen bg-[#000000] text-white flex flex-col items-center justify-center p-8 relative"
    >
      {/* Netflix-style gradient overlay */}
      <div className="absolute inset-0 bg-gradient-to-b from-[#000000] via-[#000000]/80 to-[#000000] z-10" />

      {/* Background pattern */}
      <div className="absolute inset-0 bg-[#000000] opacity-50">
        <div className="absolute inset-0 bg-[url('/netflix-pattern.png')] opacity-5" />
      </div>

      <motion.h1 
        initial={{ y: -20 }}
        animate={{ y: 0 }}
        className="text-5xl font-bold mb-12 text-[#E50914] relative z-20"
      >
        Choose Your Profile
      </motion.h1>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-8 max-w-6xl w-full relative z-20">
        {profiles.map((profile, index) => (
          <motion.div
            key={profile.name}
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ delay: index * 0.2 }}
            whileHover={{ scale: 1.05 }}
            whileTap={{ scale: 0.95 }}
            className="bg-[#141414] p-8 rounded-lg cursor-pointer transform transition-all duration-300 hover:bg-[#1F1F1F] border border-[#333333] hover:border-[#E50914]"
            onClick={() => handleProfileSelect(profile)}
          >
            <div className="text-6xl mb-4">{profile.icon}</div>
            <h2 className="text-2xl font-bold mb-2 text-white">{profile.name}</h2>
            <p className="text-[#B3B3B3]">{profile.description}</p>
          </motion.div>
        ))}
      </div>
    </motion.div>
  );
};

export default ProfileSelector;
