import React, { useState } from 'react';
import { motion } from 'framer-motion';
import { useNavigate } from 'react-router-dom';

const ContactPage = () => {
  const navigate = useNavigate();
  const [copiedEmail, setCopiedEmail] = useState(false);

  const contactInfo = {
    email: "govindjiharsh@gmail.com",
    linkedin: "https://www.linkedin.com/in/harsh-govindji-1a32b5260/",
    github: "https://github.com/hgovi?tab=repositories"
  };

  const handleEmailCopy = async () => {
    try {
      await navigator.clipboard.writeText(contactInfo.email);
      setCopiedEmail(true);
      setTimeout(() => setCopiedEmail(false), 2000);
    } catch (err) {
      console.error('Failed to copy email: ', err);
    }
  };

  const handleLinkClick = (url) => {
    window.open(url, '_blank');
  };

  return (
    <motion.div 
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      className="min-h-screen bg-[#000000] text-white relative"
    >
      {/* Navigation Bar */}
      <div className="absolute top-0 left-0 right-0 z-40 backdrop-blur-sm">
        <div className="flex items-center justify-between px-4 py-2">
          {/* Logo and Navigation Links on the left */}
          <div className="flex items-center space-x-8">
            <img 
              src="/landing_page.png" 
              alt="Portfolio Logo" 
              className="h-32 cursor-pointer hover:opacity-80 transition-opacity"
              onClick={() => navigate('/')}
            />

            {/* Navigation Links next to logo */}
            <div className="flex items-center space-x-6">
              <span 
                className="text-white cursor-pointer hover:text-[#E50914] transition-colors font-medium"
                onClick={() => navigate('/profiles')}
              >
                Home
              </span>
              <span className="text-white cursor-pointer hover:text-[#E50914] transition-colors font-medium">
                Professional
              </span>
              <span className="text-white cursor-pointer hover:text-[#E50914] transition-colors font-medium">
                Skills
              </span>
              <span className="text-white cursor-pointer hover:text-[#E50914] transition-colors font-medium text-[#E50914]">
                Contact Me
              </span>
            </div>
          </div>

          {/* Profile Icon on the right */}
          <div className="w-12 h-12 bg-[#141414] rounded-lg flex items-center justify-center border border-[#333333]">
            <div className="text-2xl">ðŸ‘”</div>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div className="pt-40 px-4">
        <div className="max-w-6xl mx-auto">
          {/* Header Section */}
          <motion.div 
            initial={{ y: 20, opacity: 0 }}
            animate={{ y: 0, opacity: 1 }}
            transition={{ delay: 0.2 }}
            className="text-center mb-16"
          >
            <h1 className="text-6xl font-bold text-white mb-6">Get In Touch</h1>
            <p className="text-xl text-[#B3B3B3] max-w-2xl mx-auto">
              I'm always open to discussing new opportunities, interesting projects, or just having a chat about technology and data science.
            </p>
          </motion.div>

          {/* Contact Cards Grid */}
          <div className="grid grid-cols-1 md:grid-cols-3 gap-8">
            {/* Email Card */}
            <motion.div
              initial={{ y: 30, opacity: 0 }}
              animate={{ y: 0, opacity: 1 }}
              transition={{ delay: 0.4 }}
              className="bg-[#141414] rounded-lg p-8 hover:bg-[#1F1F1F] transition-colors duration-300 border border-[#333333] hover:border-[#E50914] cursor-pointer"
              onClick={handleEmailCopy}
            >
              <div className="text-center">
                <div className="w-16 h-16 bg-[#E50914] rounded-full flex items-center justify-center mx-auto mb-6">
                  <svg className="w-8 h-8 text-white" fill="currentColor" viewBox="0 0 24 24">
                    <path d="M20 4H4c-1.1 0-1.99.9-1.99 2L2 18c0 1.1.9 2 2 2h16c1.1 0 2-.9 2-2V6c0-1.1-.9-2-2-2zm0 4l-8 5-8-5V6l8 5 8-5v2z"/>
                  </svg>
                </div>
                <h3 className="text-2xl font-bold text-white mb-4">Email</h3>
                <p className="text-[#B3B3B3] mb-4">Let's start a conversation</p>
                <div className="bg-[#1F1F1F] rounded-lg p-4">
                  <p className="text-white font-mono text-sm break-all">
                    {contactInfo.email}
                  </p>
                </div>
                {copiedEmail && (
                  <motion.div
                    initial={{ opacity: 0, y: 10 }}
                    animate={{ opacity: 1, y: 0 }}
                    className="mt-4 text-[#E50914] text-sm font-medium"
                  >
                    âœ“ Email copied to clipboard!
                  </motion.div>
                )}
              </div>
            </motion.div>

            {/* LinkedIn Card */}
            <motion.div
              initial={{ y: 30, opacity: 0 }}
              animate={{ y: 0, opacity: 1 }}
              transition={{ delay: 0.6 }}
              className="bg-[#141414] rounded-lg p-8 hover:bg-[#1F1F1F] transition-colors duration-300 border border-[#333333] hover:border-[#E50914] cursor-pointer"
              onClick={() => handleLinkClick(contactInfo.linkedin)}
            >
              <div className="text-center">
                <div className="w-16 h-16 bg-[#0077B5] rounded-full flex items-center justify-center mx-auto mb-6">
                  <svg className="w-8 h-8 text-white" fill="currentColor" viewBox="0 0 24 24">
                    <path d="M20.447 20.452h-3.554v-5.569c0-1.328-.027-3.037-1.852-3.037-1.853 0-2.136 1.445-2.136 2.939v5.667H9.351V9h3.414v1.561h.046c.477-.9 1.637-1.85 3.37-1.85 3.601 0 4.267 2.37 4.267 5.455v6.286zM5.337 7.433c-1.144 0-2.063-.926-2.063-2.065 0-1.138.92-2.063 2.063-2.063 1.14 0 2.064.925 2.064 2.063 0 1.139-.925 2.065-2.064 2.065zm1.782 13.019H3.555V9h3.564v11.452zM22.225 0H1.771C.792 0 0 .774 0 1.729v20.542C0 23.227.792 24 1.771 24h20.451C23.2 24 24 23.227 24 22.271V1.729C24 .774 23.2 0 22.222 0h.003z"/>
                  </svg>
                </div>
                <h3 className="text-2xl font-bold text-white mb-4">LinkedIn</h3>
                <p className="text-[#B3B3B3] mb-4">Connect professionally</p>
                <div className="bg-[#1F1F1F] rounded-lg p-4">
                  <p className="text-white text-sm">View my professional profile</p>
                </div>
              </div>
            </motion.div>

            {/* GitHub Card */}
            <motion.div
              initial={{ y: 30, opacity: 0 }}
              animate={{ y: 0, opacity: 1 }}
              transition={{ delay: 0.8 }}
              className="bg-[#141414] rounded-lg p-8 hover:bg-[#1F1F1F] transition-colors duration-300 border border-[#333333] hover:border-[#E50914] cursor-pointer"
              onClick={() => handleLinkClick(contactInfo.github)}
            >
              <div className="text-center">
